function gerarSolicitacao ()
{
    let x0 = ``;
    let x = ``;
    if (escola == "") return alert("Insira o nome da Escola.");
    else {
    x0 = `Prezados organizadores da ${evento},

Nós, da instituição ${escola}, gostaríamos de solicitar o agendamento para participação no evento nas seguintes datas:`};    
    if (professor1 != "" && telefone1 != "" && alunos1 != nulo && escolaridade1 != nulo && opcao11 != nulo) {
